DROP TABLE IF EXISTS main.rehearsal_points;
CREATE TABLE main.rehearsal_points (id INTEGER PRIMARY KEY, rating REAL, contact_number VARCHAR(255), schedule JSON, name TEXT, address TEXT);


DROP TABLE IF EXISTS main.equipment;
CREATE TABLE main.equipment (id INTEGER PRIMARY KEY, name TEXT, type TEXT, brand TEXT, model TEXT, condition TEXT, id_rehearsal_point INTEGER);


DROP TABLE IF EXISTS main.rooms;
CREATE TABLE main.rooms (id INTEGER PRIMARY KEY, name TEXT, air_conditioner BOOLEAN, price INTEGER, recording_support BOOLEAN, area INTEGER, id_rehearsal_point INTEGER);


DROP TABLE IF EXISTS main.staff;
CREATE TABLE main.staff (id INTEGER PRIMARY KEY, full_name TEXT, address TEXT, experience INTEGER, phone VARCHAR(255), age INTEGER, id_rehearsal_point INTEGER);


DROP TABLE IF EXISTS main.users;
CREATE TABLE main.users (id INTEGER PRIMARY KEY, full_name TEXT, phone VARCHAR(255), email TEXT, registration_date TIMESTAMP);


DROP TABLE IF EXISTS main.service_booking;
CREATE TABLE main.service_booking (id_service INTEGER, id_booking INTEGER);


DROP TABLE IF EXISTS main.equipment_booking;
CREATE TABLE main.equipment_booking (id_equipment INTEGER, id_booking INTEGER);


DROP TABLE IF EXISTS main.booking;
CREATE TABLE main.booking (id INTEGER PRIMARY KEY, time TIMESTAMP, cost INTEGER, creation_date TIMESTAMP, status VARCHAR(255), number_of_people INTEGER, id_room INTEGER, id_user INTEGER, duration INTEGER);


DROP TABLE IF EXISTS main.service;
CREATE TABLE main.service (id INTEGER PRIMARY KEY, name TEXT, price INTEGER, type TEXT, requirements TEXT, id_rehearsal_point INTEGER);


